mapper.select:
SELECT u.uid,u.nickname, u.avatar, DATE_FORMAT(u.spread_time, '%Y-%m-%d %H:%i:%s') AS `time`, u.spread_count AS childCount, (SELECT count( * ) AS num FROM eb_store_order AS o RIGHT JOIN eb_user_brokerage_record AS br ON br.link_id = o.order_id AND br.STATUS = 3 WHERE o.uid = u.uid AND br.uid = u.spread_uid AND o.STATUS > 1) AS orderCount, (SELECT IFNULL( sum( o.pay_price ), 0 ) AS price FROM eb_store_order AS o RIGHT JOIN eb_user_brokerage_record AS br ON br.link_id = o.order_id AND br.STATUS = 3 WHERE o.uid = u.uid AND br.uid = u.spread_uid AND o.STATUS > 1) AS numberCount FROM eb_user AS u where u.uid in
#{uid}
and ( u.real_name like CONCAT('%',#{keywords},'%') or u.nickname like CONCAT('%',#{keywords},'%'))
ORDER BY ${sortKey} ${sortValue}

SELECT u.* FROM eb_user AS u

    INNER JOIN eb_user_token AS ut on u.uid = ut.uid and ut.type = 1


    INNER JOIN eb_user_token AS ut on u.uid = ut.uid and ut.type = 2

where 1 = 1

    and u.is_promoter = #{isPromoter}


    and u.group_id in

        #{group_id}



    and ${tagIdSql}


    and u.level in

        #{level}



    and u.sex = #{sex}


    and u.country = #{country}


    and u.addres like CONCAT('%',#{addres},'%')



        and u.pay_count = 0


        and u.pay_count >= ${payCount}



    and u.`status` = ${status}




            and create_time between #{startTime} and #{endTime}
            and create_time = last_login_time


            and last_login_time between #{startTime} and #{endTime}


            and last_login_time not between #{startTime} and #{endTime}


            and last_login_time between #{startTime} and #{endTime}




    and ( u.phone like CONCAT('%',#{keywords},'%') or u.nickname like CONCAT('%',#{keywords},'%') or u.mark like CONCAT('%',#{keywords},'%'))

ORDER BY u.uid desc
